function filterBtn(){document.querySelector(".catalog__filters")&&(document.querySelector(".catalog__filters").addEventListener("click",e=>{document.querySelector(".filter-popup").classList.add("active"),document.querySelector(".background-blur").classList.add("active")}),document.querySelector(".filter-popup__title-cross").addEventListener("click",e=>{document.querySelector(".filter-popup").classList.remove("active"),document.querySelector(".background-blur").classList.remove("active")}))}function handlerSwitch(){document.querySelectorAll(".switch").forEach(e=>{let t=e.querySelectorAll(".switch__btn"),c=e.querySelector(".switch__bg");t.forEach(e=>{e.classList.contains("active")&&(c.style.left=e.offsetLeft+"px",c.style.width=getComputedStyle(e).width),e.addEventListener("click",o=>{t.forEach(e=>{e.classList.remove("active")}),e.classList.add("active"),c.style.left=e.offsetLeft+"px",c.style.width=getComputedStyle(e).width})})})}function openPopup(){document.querySelectorAll(".catalog__item-card").forEach(e=>{e.addEventListener("click",e=>{document.querySelector(".current-popup").classList.add("active"),document.querySelector(".background-blur").classList.add("active")}),document.querySelectorAll(".current-popup__cross").forEach(e=>{e.addEventListener("click",e=>{document.querySelector(".current-popup").classList.remove("active"),document.querySelector(".background-blur").classList.remove("active")})})})}function openMobileMenu(){document.querySelector(".menu__burger").addEventListener("click",e=>{document.querySelector(".menu__burger").classList.toggle("active"),document.querySelector(".mobile-menu").classList.toggle("active"),document.querySelector(".background-blur").classList.toggle("active")})}document.addEventListener("DOMContentLoaded",(function(){filterBtn(),handlerSwitch(),openPopup(),openMobileMenu()}));